test("Level1 fetches successfully data from an API", async () => {
  mockedAxios.get.mockResolvedValue({
    data: mockData,
  });

  const { result, waitForNextUpdate } = renderHook(() => useLevel1Data());

  await act(async () => {
    await waitForNextUpdate();
  });
  expect(result.current.data).toEqual(mockData.regions);
  expect(result.current.loading).toBe(false);
  expect(result.current.error).toBe(null);
});

test("response.data empty", async () => {
  mockedAxios.get.mockResolvedValue({ data: {} });

  const { result, waitForNextUpdate } = renderHook(() => useLevel1Data());

  await act(async () => {
    await waitForNextUpdate();
  });

  expect(result.current.loading).toBe(false);
  expect(result.current.data).toStrictEqual([]);
  expect(result.current.error).toEqual("Data is empty or invalid.");
});

