test("Invalid centroid data for region", async () => {
  mockedAxios.get.mockResolvedValue({
    data: noCentroid,
  });
  const { result, waitForNextUpdate } = renderHook(() => useLevel1Data());

  await act(async () => {
    await waitForNextUpdate();
  });

  expect(result.current.loading).toBe(false);
  expect(result.current.data).toStrictEqual([]);
  expect(result.current.error).toEqual("Invalid centroid data for region");
});

const noPolygon: any = {
  regions: [
    {
      id: 1,
      name: "Africa",
      centroid: "[17.458740234362434, -2.677413176352464]",
    },
  ],
};

test("Invalid data", async () => {
  mockedAxios.get.mockResolvedValue({
    data: noPolygon,
  });
  const { result, waitForNextUpdate } = renderHook(() => useLevel1Data());

  await act(async () => {
    await waitForNextUpdate();
  });

  expect(result.current.loading).toBe(false);
  expect(result.current.error).toEqual("Data is not loaded");
});
